<?php

namespace Tests\Feature\Api\External\Settings;

use App\Models\Settings;
use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Foundation\Testing\WithFaker;
use Tests\TestCase;

class SettingsControllerTest extends TestCase
{
    protected function setUp(): void
    {
        parent::setUp(); // TODO: Change the autogenerated stub

        Settings::factory(1)->create();
    }

    public function testGettingAGeneralSettings()
    {
        $req = $this->get('/api/v1/settings/general');
        $req->assertOk();
    }

    public function testGettingAboutCompany()
    {
        $req = $this->get('/api/v1/settings/about-company');
        $req->assertOk();
    }
}
